import React, { useState, useCallback } from "react";

import { createEditor, Transforms, Editor, Range,Node } from "slate";

import { Slate, Editable, withReact } from "slate-react";

export default function MyEditor() {
  const [initialValue, setInitialvalue] = useState([
    {
      type: "paragraph",
      children: [{ text: "A line of text in a paragraph." }],
    },
  ]);
  const [codebutton, setcodebutton] = useState(false);
  const [editor] = useState(() => withReact(createEditor()));

  function codeb() {
    if (codebutton == false) {
      setcodebutton(true);
      Transforms.insertNodes(editor, {
        type: "codei",
        children: [{ text: "" }],
      });
    } else {
      setcodebutton(false);
      Transforms.insertNodes(editor, {
        type: "paragraph",
        children: [{ text: "" }],
      });
    }
  }

  const renderElement = useCallback(({ attributes, children, element }) => {
    switch (element.type) {
      case "codei":
        return (
          <pre
            {...attributes}
            style={{
              fontFamily: "Sans-serif",
              color: "red",
              backgroundColor: "black",
              margin: "0px",
            }}
          >
            {children}
          </pre>
        );
        case  'table':
            return(
              <table {...attributes} style = {{width:"100%" ,border:"1px solid"}}>{children}</table>


            );
        case 'table-rows':
            return(
              <tr {...attributes} style = {{border:"1px solid"}}>{children}</tr>

            );
         case 'table-col':
          return(
            <td {...attributes} style = {{border:"1px solid"}}>{children}</td>
          )   
          case 'Body':
            return(
              <tbody {...attributes}>{children}</tbody>
            )
      default:
        return <p {...attributes}>{children}</p>;
    }
  }, []);
  const ele = [
{
data :{
row:2,
col : 2
},
type : 'table',
children:[{type : 'Body',children : [{type : 'table-rows',
children : [{type : 'table-col',children :[{type : 'paragraph',children:[{text:'2'}]}]},{type : 'table-col',children :[{type : 'paragraph',children:[{text:'1'}]}]}]},
{type : 'table-rows',
children : [{type : 'table-col',children :[{type : 'paragraph',children:[{text:'3'}]}]},{type : 'table-col',children :[{type : 'paragraph',children:[{text:'4'}]}]}]}]
}]


}



  ]
function Table(){

Transforms.insertNodes(editor,ele);

}
function InsertRow(){
const path = editor.selection.anchor.path;
let a = path.length;
let b = [];
for(let i = 0;i<a-3;i++){
b.push(path[i]);
}
let c = [];

for(let i = 0;i<a-5;i++){
c.push(path[i]);

}
console.log(c);
const [s,e] = Editor.edges(editor, c);
console.log(e);
let ncol = e.path[e.path.length-3]+1;
console.log(ncol);
const row1 = {type : 'table-rows',children : []};
for(let i = 0;i<ncol;i++){
row1.children.push({type : 'table-col',children :[{type : 'paragraph',children:[{text:'3'}]}]})
}

b[a-4]+=1;
Transforms.insertNodes(editor,row1,{at : b});

}
function loc(){
console.log(Editor.edges(editor,[1]));

}
function insertCol(){
  const path = editor.selection.anchor.path;
  let a = path.length;

  let c = [];

  for(let i = 0;i<a-5;i++){
  c.push(path[i]);

  }

  const [s,e] = Editor.edges(editor, c);
let rowpl = [];
for(let i = 0;i<a-4;i++){
  rowpl.push(e.path[i]);
}
const[node,path1] = Editor.node(editor,{at : rowpl});
console.log(node);
for(const[noder,path2] of Node.children(node,rowpl)){
let ncpath = [];
for(let i = 0;i<path2.length;i++){
  ncpath.push(path2[i]);
}
ncpath.push(noder.children.length);
Transforms.insertNodes(editor,{type : 'table-col',children :[{type : 'paragraph',children:[{text:'3'}]}]},ncpath);

}

}
  return (
    <Slate
      editor={editor}
      initialValue={initialValue}
      onChange={(v) => setInitialvalue(v)}
    >
      <Editable
        renderElement={renderElement}
        onKeyDown={(event) => {
          if (event.code == "ArrowDown") {
            const range = editor.selection;

            const b = Editor.end(editor, []);
            let a1 = range.anchor.path.toString();
            let a2 = b.path.toString();
let ins  = [];
ins.push(b.path[0]+1);
console.log(ins);
            if (a1 === a2) {
              Editor.insertNode(editor, {
                type: "paragraph",
                children: [{ text: "" }],
              }, ins);
            }
          }
        }}
      />

      <button onClick={codeb}>Code-block</button>
      <button onClick={Table}>Add-Table</button>
      <button onClick = {InsertRow}>Add-Row</button>
      <button onClick = {loc}>SEELOC</button>
      <button onClick = {insertCol}>Add-Col</button>
    </Slate>
  );
}
