import React, { useState,useCallback } from 'react'

import { createEditor,Transforms,Editor,Range  } from 'slate'

import { Slate, Editable, withReact} from 'slate-react'



export default function MyEditor(){
  const [initialValue,setInitialvalue] = useState([
    {
      type: 'paragraph',
      children: [{ text: 'A line of text in a paragraph.' }],
    }
  ]);
  const[codebutton,setcodebutton] = useState(false);
    const [editor] = useState(() => withReact(createEditor()))
    function add(){
      Editor.deleteFragment(editor,"abc");
      }
      function loc(){

       const [a,b] = Editor.edges(editor,[]);
       console.log(a);
       console.log(b);
      }
      function leaf(){
const a = Editor.string(editor,[]);
console.log(a);

      }
      function insert(){
        Transforms.insertText(editor,"abc");
      }
      function codeb(){
        if(codebutton==false){
          setcodebutton(true);
Transforms.insertNodes(editor,{
  type : 'codei',
children : [{text : ''}]
});
        }
        else{
setcodebutton(false);
Transforms.insertNodes(editor,{
  type : 'paragraph',
  children : [{text : ''}]
})
        }

      }
//    window.addEventListener('keydown',(event)=>{
//   if(event.code == "ArrowDown"){  


// const range = editor.selection;


// const b = Editor.end(editor,[]);
// console.log(b.path);
// if(range.anchor.path===b.path){
//   Transforms.insertNodes(editor,{
//     type : 'paragraph',
//     children : [{text : ''}]
//   })
// }


//   }
//    })   
     const renderElement =  useCallback(({attributes,children,element})=>{
switch(element.type){
case 'codei' :
return (<pre {...attributes} style = {{fontFamily:"Sans-serif",color : "red",backgroundColor:"black",margin:"0px"}}>{children}</pre>)

default:
  return (
    <p  {...attributes} >
      {children}
    </p>
  )


}

     },[])
    return (
      <Slate editor={editor} initialValue={initialValue} onChange = {(v)=>setInitialvalue(v)}>
        <Editable renderElement={renderElement} onKeyDown={(event)=>{
           if(event.code == "ArrowDown"){  


            const range = editor.selection;


            const b = Editor.end(editor,[]);
         let a1 = range.anchor.path.stringify();
          let a2 = b.path.stringify();
            console.log(b.path);
            console.log(range.anchor.path);
            if(a1 === a2){
              console.log(range.anchor.path);
              Editor.insertNode(editor,{
                type : 'paragraph',
                children : [{text : ''}]
              })
            }


              }
        }}/>
        <button onClick = {add}>Add Node</button>
        <button onClick = {loc}>see point</button>
        <button onClick = {leaf}>see leaf</button>
        <button onClick = {insert}>insert</button>
        <button onClick = {codeb}>Code-block</button>
      </Slate>
    )
}
